mapper.insert:
INSERT INTO COMMENT(
					parent_seq
				  , writer
				  , content
	  			) 
			VALUES
	  			(
				    #{parent_seq}
				  , #{writer}
				  , #{content})

mapper.update:
UPDATE COMMENT 
SET CONTENT = #{content}
			, reg_date=now()
			, modified="Y" 
WHERE COMMENT_SEQ = ${comment_seq}
