mapper.select:
SELECT * 
FROM ROOM 
WHERE ROOMDATE = #{roomDate}
AND ROOMTYPE = #{roomType}

SELECT ROOM_REM 
FROM ROOM 
WHERE ROOMSEQ = ${roomSeq}

mapper.update:
UPDATE ROOM SET 
ROOMSTAY = ${roomStay}
WHERE ROOMSEQ = ${roomSeq}

UPDATE ROOM SET 
ROOMDATE = #{roomDate}
		   , roomStay = 50 
WHERE ROOMSEQ = ${roomSeq}
