mapper.insert:
INSERT 
INTO BOARD(
		    title
		  , content
		  , writer
		  , cnt
		  , security
		  , filepath
		  ) 
	VALUES(
		    #{title}
		  , #{content}
		  , #{writer}
		  , 0
		  , #{security}
		  , #{filepath}
		  )

mapper.update:
UPDATE BOARD 
SET TITLE = #{title}
  , content = #{content}
WHERE SEQ = ${seq}

mapper.select:
SELECT WRITER 
FROM BOARD 
WHERE SEQ = ${seq} 
AND WRITER = #{writer}
